package week1.day2;

public class ForLoopExcerciseWithIfCondition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for (int i = 1; i <= 10; i++) {
            
            // Check if the number is even or odd
            if (i % 2 == 0) {
                System.out.println(i + " is Even");
            } else {
                System.out.println(i + " is Odd");
            }
        }

	}

}
