package week1.day2;

public class LearnForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//int num=1;
		/**
		 * sysntax: for(initialization;condition:iteration)
		 * shotcut for for, type for and ctrl+space
		 */
		for (int i =1; i<=10; i++) {
			System.out.println(i);
			
		}
		System.out.println("-----------------");
		for (int i = 10; i >= 1; i--) {
			System.out.println(i);
		}

	}

}
